DROP TABLE IF EXISTS `system_logs`;
CREATE TABLE `system_logs` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `level` enum('debug','info','warning','error','critical') NOT NULL DEFAULT 'info',
  `module` varchar(50) NOT NULL,
  `action` varchar(100) DEFAULT NULL,
  `message` text NOT NULL,
  `context` longtext DEFAULT NULL,
  `user_id` varchar(255) DEFAULT NULL,
  `session_id` varchar(100) DEFAULT NULL,
  `ip_address` varchar(45) DEFAULT NULL,
  `user_agent` varchar(255) DEFAULT NULL,
  `request_id` varchar(36) DEFAULT NULL,
  `metadata` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL,
  `duration_ms` int(11) DEFAULT NULL,
  `memory_mb` int(11) DEFAULT NULL,
  `trace_id` varchar(36) DEFAULT NULL,
  `is_resolved` tinyint(1) NOT NULL DEFAULT 0,
  `resolved_at` timestamp NULL DEFAULT NULL,
  `resolved_by` varchar(255) DEFAULT NULL,
  `resolution_notes` text DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `system_logs_level_created_at_index` (`level`,`created_at`),
  KEY `system_logs_module_created_at_index` (`module`,`created_at`),
  KEY `system_logs_created_at_level_index` (`created_at`,`level`),
  KEY `system_logs_is_resolved_index` (`is_resolved`),
  KEY `system_logs_user_id_created_at_index` (`user_id`,`created_at`),
  KEY `logs_level_module_date_idx` (`level`,`module`,`created_at`),
  KEY `system_logs_module_index` (`module`),
  KEY `system_logs_user_id_index` (`user_id`),
  KEY `system_logs_session_id_index` (`session_id`),
  KEY `system_logs_ip_address_index` (`ip_address`),
  KEY `system_logs_request_id_index` (`request_id`),
  KEY `system_logs_trace_id_index` (`trace_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

